mapper.select:
select 

,

from area_spot
where area_id = #{areaId,jdbcType=VARCHAR}
  and spot_id = #{spotId,jdbcType=VARCHAR}

mapper.delete:
delete from area_spot
where area_id = #{areaId,jdbcType=VARCHAR}
  and spot_id = #{spotId,jdbcType=VARCHAR}

mapper.update:
update area_spot
set spot_name = #{spotName,jdbcType=VARCHAR},
  spot_url = #{spotUrl,jdbcType=VARCHAR},
  spot_historyurl = #{spotHistoryurl,jdbcType=VARCHAR},
  spot_obserargs = #{spotObserargs,jdbcType=VARCHAR},
  spot_obsermeth = #{spotObsermeth,jdbcType=VARCHAR},
  spot_posinfo = #{spotPosinfo,jdbcType=VARCHAR},
  spot_runtime = #{spotRuntime,jdbcType=VARCHAR}
where area_id = #{areaId,jdbcType=VARCHAR}
  and spot_id = #{spotId,jdbcType=VARCHAR}
