mapper.select:
select 

from box_sensor
where area_id = #{areaId,jdbcType=VARCHAR}
  and spot_id = #{spotId,jdbcType=VARCHAR}
  and node_id = #{nodeId,jdbcType=VARCHAR}
  and data_time = #{dataTime,jdbcType=TIMESTAMP}

mapper.delete:
delete from box_sensor
where area_id = #{areaId,jdbcType=VARCHAR}
  and spot_id = #{spotId,jdbcType=VARCHAR}
  and node_id = #{nodeId,jdbcType=VARCHAR}
  and data_time = #{dataTime,jdbcType=TIMESTAMP}

mapper.update:
update box_sensor
set Conductivity = #{conductivity,jdbcType=REAL},
  Temperature = #{temperature,jdbcType=REAL},
  Pressure = #{pressure,jdbcType=REAL},
  Salinity = #{salinity,jdbcType=REAL},
  DissolvedOxygen = #{dissolvedoxygen,jdbcType=REAL},
  SPAD = #{spad,jdbcType=REAL},
  Turbidity = #{turbidity,jdbcType=REAL},
  AnDan = #{andan,jdbcType=REAL},
  YaXiaoDan = #{yaxiaodan,jdbcType=REAL},
  LinSuanYan = #{linsuanyan,jdbcType=REAL},
  ORP = #{orp,jdbcType=REAL}
where area_id = #{areaId,jdbcType=VARCHAR}
  and spot_id = #{spotId,jdbcType=VARCHAR}
  and node_id = #{nodeId,jdbcType=VARCHAR}
  and data_time = #{dataTime,jdbcType=TIMESTAMP}
