mapper.select:
select 

,

from spot_node
where node_id = #{nodeId,jdbcType=VARCHAR}
  and spot_id = #{spotId,jdbcType=VARCHAR}
  and area_id = #{areaId,jdbcType=VARCHAR}

mapper.delete:
delete from spot_node
where node_id = #{nodeId,jdbcType=VARCHAR}
  and spot_id = #{spotId,jdbcType=VARCHAR}
  and area_id = #{areaId,jdbcType=VARCHAR}

mapper.insert:
insert into spot_node (node_id, spot_id, area_id, 
  node_name, node_type, node_note
  )
values (#{nodeId,jdbcType=VARCHAR}, #{spotId,jdbcType=VARCHAR}, #{areaId,jdbcType=VARCHAR}, 
  #{nodeName,jdbcType=VARCHAR}, #{nodeType,jdbcType=VARCHAR}, #{nodeNote,jdbcType=LONGVARCHAR}
  )

mapper.update:
update spot_node


    node_name = #{nodeName,jdbcType=VARCHAR},


    node_type = #{nodeType,jdbcType=VARCHAR},


    node_note = #{nodeNote,jdbcType=LONGVARCHAR},


where node_id = #{nodeId,jdbcType=VARCHAR}
  and spot_id = #{spotId,jdbcType=VARCHAR}
  and area_id = #{areaId,jdbcType=VARCHAR}

update spot_node
set node_name = #{nodeName,jdbcType=VARCHAR},
  node_type = #{nodeType,jdbcType=VARCHAR},
  node_note = #{nodeNote,jdbcType=LONGVARCHAR}
where node_id = #{nodeId,jdbcType=VARCHAR}
  and spot_id = #{spotId,jdbcType=VARCHAR}
  and area_id = #{areaId,jdbcType=VARCHAR}

update spot_node
set node_name = #{nodeName,jdbcType=VARCHAR},
  node_type = #{nodeType,jdbcType=VARCHAR}
where node_id = #{nodeId,jdbcType=VARCHAR}
  and spot_id = #{spotId,jdbcType=VARCHAR}
  and area_id = #{areaId,jdbcType=VARCHAR}
