mapper.select:
select 

from nantong_tuanjiehe_buoy_dianya
where area_id = #{areaId,jdbcType=VARCHAR}
  and spot_id = #{spotId,jdbcType=VARCHAR}
  and node_id = #{nodeId,jdbcType=VARCHAR}
  and data_time = #{dataTime,jdbcType=TIMESTAMP}

mapper.delete:
delete from nantong_tuanjiehe_buoy_dianya
where area_id = #{areaId,jdbcType=VARCHAR}
  and spot_id = #{spotId,jdbcType=VARCHAR}
  and node_id = #{nodeId,jdbcType=VARCHAR}
  and data_time = #{dataTime,jdbcType=TIMESTAMP}

mapper.update:
update nantong_tuanjiehe_buoy_dianya


    dianya = #{dianya,jdbcType=VARCHAR},


    dianliust = #{dianliust,jdbcType=VARCHAR},


    state = #{state,jdbcType=VARCHAR},


where area_id = #{areaId,jdbcType=VARCHAR}
  and spot_id = #{spotId,jdbcType=VARCHAR}
  and node_id = #{nodeId,jdbcType=VARCHAR}
  and data_time = #{dataTime,jdbcType=TIMESTAMP}

update nantong_tuanjiehe_buoy_dianya
set dianya = #{dianya,jdbcType=VARCHAR},
  dianliust = #{dianliust,jdbcType=VARCHAR},
  state = #{state,jdbcType=VARCHAR}
where area_id = #{areaId,jdbcType=VARCHAR}
  and spot_id = #{spotId,jdbcType=VARCHAR}
  and node_id = #{nodeId,jdbcType=VARCHAR}
  and data_time = #{dataTime,jdbcType=TIMESTAMP}
